import React, { Component } from "react";

class WidgetList extends Component {
  state = {
    languages: 
    [
      {
        id: 0,
        name: "JavaScript",
        count: 0
      },
      {
        id: 1,
        name: "Python",
        count: 0
      },
      {
        id: 2,
        name: "Go",
        count: 0
      },
      {
        id: 3,
        name: "Java",
        count: 0
      },
      {
        id: 4,
        name: "Typescript",
        count: 0
      },
      {
        id: 5,
        name: "Ruby",
        count: 0
      },
      {
        id: 6,
        name: "Elixir",
        count: 0
      }
    ]
  };

  upvoteCount = (langId) => {
    this.setState({languages: this.state.languages[langId].count+1})
  }

  render() {
    return (
        <div>
            <ul>
                {this.state.languages.map(langItem => <li key={langItem.id}>{langItem.name} <button onClick={this.upvoteCount(langItem.id)}>vote</button></li>)}
            </ul>
        </div>
    )
  }
}

export default WidgetList;
