import React, { Component } from "react";

class WidgetList extends Component {
  state = {
    languages: [
      {
        id: 0,
        name: "JavaScript",
        count: 0,
      },
      {
        id: 1,
        name: "Python",
        count: 0,
      },
      {
        id: 2,
        name: "Go",
        count: 0,
      },
      {
        id: 3,
        name: "Java",
        count: 0,
      },
      {
        id: 4,
        name: "Typescript",
        count: 0,
      },
      {
        id: 5,
        name: "Ruby",
        count: 0,
      },
      {
        id: 6,
        name: "Elixir",
        count: 0,
      },
    ],
  };

  createWidgetItem = (obj) => {
    return document.createElement("li").innerHTML = `<div className="language">${obj.count}    ${obj.name} <button onClick={this.increaseVote}>vote</button>></div>`;
  }

  upvoteCount = (langId) => {
    this.setState({languages: this.state.languages[langId].count+1})
  }

  render() {
    return (
        <div>
            <ul className="languages">
                {this.state.languages.map(lang => <li key={lang.id}>{lang.name} <button onClick={this.upvoteCount(lang.id)}>vote</button></li>)}

            </ul>
        </div>
    );
  }
}

export default WidgetList;
